import React, {useState, useContext} from "react";
import { Redirect } from "react-router-dom";
import Jumbotron from "../../components/Jumbotron/Jumbotron";
import Footer from "../../components/Footer/Footer"
import SignIn from "../../components/SignIn/SignIn";
import SignUp from "../../components/SignUp/SignUp";
import {UserContext} from "../../utils/UserContext";


const styles = {
  keepIt: {
    maxWidth: '100%',
    paddingLeft: 0,
    paddingRight: 0,
  },
  buttonSpacer: {
    marginTop: 20,
    marginBottom: 20
  }
}

export default function Landing() {

  const {globalUserName, setGlobalUserName} = useContext(UserContext);
  const [signIn, setSignIn] = useState(false);

  function checkLogin() {
    if (globalUserName) {
      return <Redirect to="/events"/>
    }
    // globalUserName ? return <Redirect to="/events"/> : console.log("this user is not logged in")
  }

    return (
        <div className="container" style={styles.keepIt}>
          <Jumbotron />
          <div className="row justify-content-center">
            <button className="btn btn-outline-secondary btn-sm" style={styles.buttonSpacer} onClick={e => setSignIn(true)}>Sign up for a new account here.</button>
          </div>
          {(signIn && <SignUp login={checkLogin()} />) || <SignIn login={checkLogin()}/>}
          <Footer />
        </div>
    )
}
